import {
  Box,
  Stack,
  Typography,
  Modal,
  IconButton,
  TextField,
  Button,
} from "@mui/material";
import FeedbackIcon from '@mui/icons-material/Feedback';
import CloseIcon from "@mui/icons-material/Close";
import { useState } from "react";
import { bgcolor, display, positions, width } from "@mui/system";

export default function FeedbackModal({
  open,
  handleClose,
  chatId,
  updateChat,
}) {
  const [input, setInput] = useState("");

  const style = {
    position: "absolute",
    top: "50%",
    left: "50%",
    transform: "translate(-50%,-50%)",
    width: "95%",
    bgcolor: "primary.bgtheme",
    boxShadow: 24,
    p: { xs: 2, md: 3 },
    maxWidth: 720,
    borderRadius: "10px",
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    updateChat((prev) => {
      prev.map((item) => {
        if (item.id == chatId) {
          return { ...item, feedback: input };
        } else {
          return { ...item };
        }
      });
    });
    setInput("");
    handleClose();
  };
  return (
    <Modal open={open} onClose={handleClose}>
      <Box sx={style}>
        <Stack
          direction={"row"}
          spacing={2}
          alignItems={"center"}
          justifyContent={"space-between"}
        >
          <Stack
            direction={"row"}
            spacing={{ xs: 0.5, md: 2 }}
            alignItems={"center"}
          >
            <FeedbackIcon />
            <Typography variant={"heading"} fontSize={{ xs: 14, md: 18 }}>
              Provide Additional Feedback
            </Typography>
          </Stack>

          <IconButton onClick={handleClose}>
            {" "}
            <CloseIcon />{" "}
          </IconButton>
        </Stack>
        <Box
          component="form"
          pt={3}
          x={{
            display: "flex",
            flexDirection: "column",
            alignItems: "flex-end",
            gap: "12px",
          }}
          onSubmit={handleSubmit}
        >
          <TextField
            multiline
            rows={6}
            sx={{ width: 1 }}
            value={input}
            onChange={(e) => setInput(e.target.value)}
            required
          />
          <Button variant="contained" type="submit">
            Submit
          </Button>
        </Box>
      </Box>
    </Modal>
  );
}
